var cartTotal=localStorage.getItem("checkout"),additemButtons=document.querySelectorAll(".addtocart");additemButtons.forEach(t=>{t.addEventListener("click",addToCart)}),document.querySelector(".cart-quantity").innerHTML=cartTotal;var clearBtn=document.querySelector(".clearStorage");function addToCart(){var t=localStorage.getItem("checkout");t++,localStorage.setItem("checkout",t),document.querySelector(".cart-quantity").innerHTML=t}function ClearCart(){localStorage.setItem("checkout",0),document.querySelector(".cart-quantity").innerHTML=cartTotal}clearBtn.addEventListener("click",ClearCart,!1);